mapper.select:
SELECT FROM goods g LEFT JOIN goods_detail d ON g.id=d.gid LEFT JOIN goods_strategy s ON g.id=s.gid LEFT JOIN strategy t ON s.sid=t.id LEFT JOIN goods_brand b ON b.id=g.bid LEFT JOIN goods_img i ON g.id=i.gid LEFT JOIN goods_type p ON p.id = g.tid where 1=1 AND g.state=1
AND b.id IN
#{item}
AND p.id IN
#{item}
AND g.price >= #{beginprice}
AND g.price <= #{endprice}
ORDER BY g.volume DESC
ORDER BY g.price DESC
ORDER BY g.volume DESC
ORDER BY g.addtime DESC
