mapper.update:
	update flash_sale
      set gid = #{gid},sku_id = #{skuId},stock = #{stock},
      current_price = #{currentPrice},shows = #{shows},
      show_time = #{showTime},activity_time = #{activityTime},
      flash_status = #{flashStatus}
    where id = #{id}

mapper.select:
SELECT ,g.name g_name,g.path g_path,k.properties_value k_properties_value from flash_sale fs LEFT JOIN goods g on fs.gid=g.id LEFT JOIN goods_sku k on fs.sku_id = k.id where 1=1
and g.mid=#{MID}
order by fs.activity_time desc limit #{from}, #{size}
