mapper.update:
update refunds
set status_code = #{status.code}, status_name = #{status.name}
where id = #{RID}

mapper.select:
select from refunds r inner join order_goods og on r.ogid = og.id inner join orders o on r.oid = o.id left join goods_img img on og.gid = img.gid
r.mid = #{mid}
and r.status_code = #{statuscode}
and r.addtime >= #{fromdate}
and ( og.gname like CONCAT('%','${keywords}','%') or o.orderno like CONCAT('%','${keywords}','%') or r.mid like CONCAT('%','${keywords}','%') or o.mname like CONCAT('%','${keywords}','%') )
order by r.addtime desc limit #{from}, #{size}

select from refunds r inner join order_goods og on r.ogid = og.id inner join orders o on r.oid = o.id left join goods_img img on og.gid = img.gid where r.uid = #{uid}
and r.status_code = #{statuscode}
and r.addtime >= #{fromdate}
and ( og.gname like CONCAT('%','${keywords}','%') or o.orderno like CONCAT('%','${keywords}','%') or r.mid like CONCAT('%','${keywords}','%') or o.mname like CONCAT('%','${keywords}','%') )
order by r.addtime desc limit #{from}, #{size}
