mapper.select:
select from order_goods og left join goods_img img on og.gid = img.gid left join orders o on og.oid = o.id where o.uid = #{uid}
and o.status_code = #{statuscode}
and o.addtime >= #{fromdate}
and ( og.gname like CONCAT('%','${keywords}','%') or o.orderno like CONCAT('%','${keywords}','%') or o.mid like CONCAT('%','${keywords}','%') or o.mname like CONCAT('%','${keywords}','%') )
order by o.addtime desc limit #{from}, #{size}

select count(1) 
from order_goods og
left join orders o on og.oid = o.id 
where 
	o.uid = #{uid}

		and o.status_code = #{statuscode}


		and o.addtime >= #{fromdate}


		and (
				og.gname like CONCAT('%','${keywords}','%') 
				or 
				o.orderno like CONCAT('%','${keywords}','%')
			)

select from order_goods og left join goods_img img on og.gid = img.gid left join orders o on og.oid = o.id
o.mid = #{mid}
and o.status_code = #{statuscode}
and o.addtime >= #{fromdate}
and ( og.gname like CONCAT('%','${keywords}','%') or o.orderno like CONCAT('%','${keywords}','%') or o.mid like CONCAT('%','${keywords}','%') or o.mname like CONCAT('%','${keywords}','%') )
order by o.addtime desc limit #{from}, #{size}

mapper.update:
update orders
set status_code = #{statuscode}, status_name = #{statusname}
where id in 

	#{oid}

mapper.select:
select o.id
from orders o
inner join order_trace ot on o.id = ot.oid and ot.status_code = #{statuscode}
where ot.addtime is not null and ot.addtime <= #{deadline} and o.status_code = #{statuscode}
