mapper.select:
select count(1) from 
favorites c
where c.type = #{type} 
 and uid = #{uid}

mapper.update:
update ad_module set
code = #{code},
name = #{name},
imgpath = #{imgpath},
description = #{description}
where id = #{ID}

mapper.select:
select id,code,name,imgpath,description from ad_module  t

	 where t.name LIKE CONCAT(CONCAT('%', #{name}), '%')


	limit #{from},#{size}

 select uid,GROUP_CONCAT(bid) as bidstr from (
	select distinct uid,bid from 
	v_recommend_goods v,goods g
	where v.gid = g.id
	and v.uid = #{uid}
	) t group by t.uid

 select uid,GROUP_CONCAT(tid) as tidstr from (
	select distinct uid,tid from 
	v_recommend_goods v,goods g
	where v.gid = g.id
	and v.uid = #{uid}
	) t group by t.uid

select g.id, g.mid, g.bid, g.name, g.code, g.tid, g.price, g.promotion_price, g.now_price, g.state, g.up_time, g.down_time, g.count, g.volume, g.addtime, g.postage, g.sub_volume_type, g.barCode, g.desc, g.title, g.admin_sort, g.merchant_sort, g.expand_sort, g.record_sort, g.pro_start, g.pro_end, g.pro_flag, i.thumb_path_1 as thumb_path_1,d.specifications from goods g left join goods_detail d on g.id = d.gid LEFT JOIN goods_img i ON g.id=i.gid where 1=1
and g.bid in
#{bid}
and g.tid in
#{tid}
LIMIT #{from} , #{to}


 select 
 g.id, g.mid, g.bid, g.name, g.code, g.tid, g.price, g.promotion_price, g.now_price, g.state, g.up_time, 
	g.down_time, g.count, g.volume, g.addtime, g.postage, g.sub_volume_type, g.barCode, g.desc, g.title, g.admin_sort,
	 g.merchant_sort, g.expand_sort, g.record_sort, g.pro_start, g.pro_end, g.pro_flag 
 from (
	select a.uid,a.gid, sum(count) as r_count From sales_records a where  exists
	(
	select 1 from sales_records b where gid = #{gid}
	and a.uid = b.uid
	)
	and a.gid <> #{gid}
	and a.uid <> #{uid}
	group by a.uid,a.gid
	order by r_count desc
	) c left join goods g
	on c.gid = g.id
	 LIMIT #{from} , #{to}
